import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	// var space = Array(9).fill('');
	const [ space, setSpace ] = useState([
		{ id: 0, value: '' },
		{ id: 1, value: '' },
		{ id: 2, value: '' },
		{ id: 3, value: '' },
		{ id: 4, value: '' },
		{ id: 5, value: '' },
		{ id: 6, value: '' },
		{ id: 7, value: '' },
		{ id: 8, value: '' }
	]);
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		setSpace();
	}

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
