import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	const space = Array(9).fill('');
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		if (turn === 'X') {
			console.log(index);
			console.log(space);
			return space[index] === '' ? space.splice(index, 1, 'x') : console.log('nope');
		}
	}

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
