import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	const [ space, setSpace ] = useState([
		{
			id: 0,
			value: ''
		},
		{
			id: 1,
			value: ''
		},
		{
			id: 2,
			value: ''
		},
		{
			id: 3,
			value: ''
		},
		{
			id: 4,
			value: ''
		},
		{
			id: 5,
			value: ''
		},
		{
			id: 6,
			value: ''
		},
		{
			id: 7,
			value: ''
		},
		{
			id: 8,
			value: ''
		}
	]);
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		if (turn === 'X') {
			setSpace([ ...space, (space[index].value = 'X') ]);
		}
	}

	console.log(space[3]);

	return (
		<div>
			<Board space={space} />
		</div>
	);
}

export default App;
