import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	const [ space, setSpace ] = useState([
		{
			value: '1'
		},
		{
			value: '2'
		},
		{
			value: '3'
		},
		{
			value: ''
		},
		{
			value: ''
		},
		{
			value: ''
		},
		{
			value: ''
		},
		{
			value: ''
		},
		{
			value: ''
		}
	]);
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		if (turn === 'X') {
			setSpace([ ...space, (space[index].value = 'x') ]);
		}
	}

	console.log(space[3]);

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
