import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	const [ space, setSpace ] = useState(Array(9).fill(''));
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		if (turn === 'X') {
			console.log(space[index], ' index', index);
			return space[index] === '' ? setSpace([ ...space, (space[index] = 'X') ]) : console.log('nope');
		}
	}

	console.log(space[0]);

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
