import React, { useState } from 'react';
import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	const { checkWin } = helpers;

	const [ space, setSpace ] = useState(Array(9).fill(null));
	const [ x, setX ] = useState([]);
	const [ y, setY ] = useState([]);
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		const updateBoard = [ ...space ];
		if (updateBoard[index] !== null) return;
		updateBoard.splice(index, 1, turn === 'X' ? 'X' : 'O');
		turn === 'X' ? setX([ ...x, index ]) : setY([ ...y, index ]);
		setSpace(updateBoard);
		setTurn(turn === 'X' ? 'O' : 'X');
		console.log('updateBoard', updateBoard);
		console.log('x', x);
		console.log('y', y);
		checkWin(updateBoard);
	}

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
