import './App.css';
//Components
import Board from './board';
//Helper
import * as helpers from './helpers';

function App() {
	// Holds all of the values for the state
	var space = Array(9).fill('');
	const [ turn, setTurn ] = useState('X');

	function capture(index) {
		console.log('hello', space);
		return space.splice(index, 1, 'hello');
	}

	return (
		<div>
			<Board space={space} capture={capture} />
		</div>
	);
}

export default App;
